CREATE INDEX index_countries_on_marine_eez_pas_geom ON public.countries USING gist (marine_eez_pas_geom);
CREATE INDEX index_countries_on_marine_pas_geom ON public.countries USING gist (marine_pas_geom);
CREATE INDEX index_countries_on_marine_ts_pas_geom ON public.countries USING gist (marine_ts_pas_geom);
CREATE INDEX index_countries_on_ts_geom ON public.countries USING gist (ts_geom);

