CREATE TABLE public.countries (
	id serial4 NOT NULL,
	name text NULL,
	iso varchar NULL,
	iso_3 varchar NULL,
	created_at timestamp NULL,
	updated_at timestamp NULL,
	"language" varchar NULL,
	region_id int4 NULL,
	bounding_box public.geometry(geometry) NULL,
	marine_pas_geom public.geometry(geometry) NULL,
	land_pas_geom public.geometry(geometry) NULL,
	land_geom public.geometry(geometry) NULL,
	eez_geom public.geometry(geometry) NULL,
	ts_geom public.geometry(geometry) NULL,
	marine_ts_pas_geom public.geometry(geometry) NULL,
	marine_eez_pas_geom public.geometry(geometry) NULL,
	country_id int4 NULL,
	is_biopama bool NOT NULL DEFAULT false,
	CONSTRAINT countries_pkey PRIMARY KEY (id)
);
CREATE INDEX index_countries_on_eez_geom ON public.countries USING gist (eez_geom);
CREATE INDEX index_countries_on_iso ON public.countries USING btree (iso);
CREATE INDEX index_countries_on_iso_3 ON public.countries USING btree (iso_3);
CREATE INDEX index_countries_on_land_geom ON public.countries USING gist (land_geom);
CREATE INDEX index_countries_on_land_pas_geom ON public.countries USING gist (land_pas_geom);
CREATE INDEX index_countries_on_marine_eez_pas_geom ON public.countries USING gist (marine_eez_pas_geom);
CREATE INDEX index_countries_on_marine_pas_geom ON public.countries USING gist (marine_pas_geom);
CREATE INDEX index_countries_on_marine_ts_pas_geom ON public.countries USING gist (marine_ts_pas_geom);
CREATE INDEX index_countries_on_ts_geom ON public.countries USING gist (ts_geom);

